import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';

function ScheduleWidget() {
  // State to manage which menu is open (one at a time only)
  const [openMenu, setOpenMenu] = React.useState(null); // 'day_index' string

  const parseTime = (timeStr) => {
    const [hours, minutes] = timeStr.split(':');
    return new Date(0, 0, 0, parseInt(hours), parseInt(minutes));
  };
  const calculateTotalHours = (from, to) => {
    const fromTime = parseTime(from);
    const toTime = parseTime(to);
    const hourDiff = (toTime - fromTime) / (1000 * 60 * 60);
    return parseFloat(Math.abs(hourDiff).toFixed(2));
  };

  // Only use incoming model
  const [schedule, setSchedule] = React.useState(appsmith.model);
  React.useEffect(() => {
    appsmith.onModelChange((newModel) => {
      setSchedule(newModel);
      setOpenMenu(null); // chiudi qualsiasi menu quando aggiorna dati
    });
  }, []);

  const handleEdit = (rowIndex) => {
    appsmith.triggerEvent("openModalOrarioEdit", {rowIndex});
  };
  const handleDelete = (day, index, rowIndex) => {
    setOpenMenu(null);
    const confirmDelete = confirm(`Sicuri di cancellare questo turno ${day}?`);
    if (confirmDelete) {
      const updatedSchedule = { ...schedule };
      updatedSchedule[day].splice(index, 1);
      setSchedule({ ...updatedSchedule });
      appsmith.triggerEvent("onDeleteOrario", {rowIndex});
    }
  };
  // Per chiudere il menu su click esterno
  React.useEffect(() => {
    function onDocClick(e) {
      // Se c'è menu aperto e si clicca fuori dal menu
      if (openMenu && !e.target.closest('.menu-popup') && !e.target.closest('.menu-trigger')) {
        setOpenMenu(null);
      }
    }
    if (openMenu) document.addEventListener('mousedown', onDocClick);
    return () => document.removeEventListener('mousedown', onDocClick);
  }, [openMenu]);

  return (
    <div className="widget-outer-wrapper">
      <div className="schedule">
        {Object.entries(schedule || {}).map(([day, slots]) => (
          <div className="day-container" key={day}>
            <div className="day-header">{day}</div>
            {(slots || []).map((slot, index) => {
              const menuKey = `${day}_${index}`;
              return (
                <div className="time-slot" key={index}>
                  <button className="menu-trigger" onClick={() => setOpenMenu(openMenu === menuKey ? null : menuKey)} title="Azioni turno" tabIndex={0} aria-label="Azioni turno">
                    <i className="fas fa-ellipsis-v"></i>
                  </button>
                  {openMenu === menuKey && (
                    <div className="menu-popup">
                      <button className="menu-action delete" onClick={() => handleDelete(day, index,slot.rowIndex)}>
                        <i className="fas fa-trash"></i> Elimina
                      </button>
                      <button className="menu-action edit" onClick={() => handleEdit(slot.rowIndex)}>
                        <i className="fas fa-pen"></i> Modifica
                      </button>
                    </div>
                  )}
                  <div className="time-details">
                    <div>
                      <span>{slot.from}</span> - <span>{slot.to}</span>
                      <div className="total-hours">
                        {calculateTotalHours(slot.from, slot.to)} ore
                      </div>
                    </div>
                  </div>
                  <div className="location-and-buttons">
                    <span>{slot.location}</span>
                  </div>
                  <div className="district">
                    <span>{slot.district}</span>
                  </div>
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
}

appsmith.onReady(() => {
  ReactDOM.render(<ScheduleWidget />, document.getElementById("root"));
});
