.time-picker-widget {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-left: auto;
  background: none;
  box-shadow: none;
  border-radius: 0;
  font-family: system-ui;
  padding: 0;
  min-width: 0;
  min-height: 0;
  overflow: visible;
}

.time-picker-label {
  font-weight: 600;
  font-size: 15px;
  color: #222;
  margin-right: 8px;
}

.time-selector {
  display: flex;
  align-items: center;
  gap: 4px;
}

.time-selector select {
  appearance: none;
  background: #f9f9f9;
  border: 1px solid #d7d7d7;
  border-radius: 4px;
  outline: none;
  transition: border-color 0.2s;
  min-width: 0;
  min-height: 0;
  overflow: visible;
}

.time-selector select:focus {
  border-color: var(--appsmith-theme-primaryColor);
}

.time-separator {
  font-size: 16px;
  font-weight: 700;
  padding: 0 3px;
  user-select: none;
}

.time-picker-xs {
  font-size: 11px;
  padding: 0;
}

.time-picker-xs select {
  padding: 2px 4px;
  font-size: 11px;
}

.time-picker-s {
  font-size: 13px;
  padding: 0;
}

.time-picker-s select {
  padding: 5px 10px;
  font-size: 13px;
}

.time-picker-m {
  font-size: 15px;
  padding: 0;
}

.time-picker-m select {
  padding: 7px 13px;
  font-size: 15px;
}

.time-picker-l {
  font-size: 16px;
  padding: 0;
}

.time-picker-l select {
  padding: 8px 12px;
  font-size: 16px;
}

.time-picker-xl {
  font-size: 20px;
  padding: 0;
}

.time-picker-xl select {
  padding: 13px 22px;
  font-size: 20px;
}

